Here's a candidate API and example of how to instantiate one scheme using it:

~~~C
#include <stddef.h>
#include <stdint.h>

#include <oqs/rand.h>

typedef struct OQS_SIGN {
	char *method_name;
	uint16_t estimated_classical_security;
	uint16_t estimated_quantum_security;
	size_t priv_len;
	size_t pub_len;
	size_t sig_len;
	int (*keygen)(OQS_RAND *rand, uint8_t *priv, uint8_t *pub);
	int (*sign)(OQS_RAND *rand, const uint8_t *priv, const uint8_t *msg, const size_t msg_len, uint8_t *sig, size_t *sig_len);
	int (*verify)(OQS_RAND *rand, const uint8_t *pub, const uint8_t *msg, const size_t msg_len, const uint8_t *sig, const size_t sig_len);
} OQS_SIGN;

int OQS_SIGN_douglas_awesome_keygen(OQS_RAND *rand, uint8_t *priv, uint8_t *pub) {
	return 1;
}
int OQS_SIGN_douglas_awesome_sign(OQS_RAND *rand, const uint8_t *priv, const uint8_t *msg, const size_t msg_len, uint8_t *sig, size_t *sig_len) {
	return 1;
}
int OQS_SIGN_douglas_awesome_verify(OQS_RAND *rand, const uint8_t *pub, const uint8_t *msg, const size_t msg_len, const uint8_t *sig, const size_t sig_len) {
	return 1;
}
OQS_SIGN OQS_SIGN_douglas_awesome = {
	"Douglas' signature scheme, awesome security level",
	512, // estimated_classical_security
	256, // estimated_quantum_security
	1024, // private key length
	2048, // public key length
	3072, // max signature length
	&OQS_SIGN_douglas_awesome_keygen,
	&OQS_SIGN_douglas_awesome_sign,
	&OQS_SIGN_douglas_awesome_verify
};

int OQS_SIGN_keygen(const OQS_SIGN *scheme, OQS_RAND *rand, uint8_t *priv, uint8_t *pub) {
	return scheme->keygen(rand, priv, pub);
}
int OQS_SIGN_sign(const OQS_SIGN *scheme, OQS_RAND *rand, const uint8_t *priv, const uint8_t *msg, const size_t msg_len, uint8_t *sig, size_t *sig_len) {
	return scheme->sign(rand, priv, msg, msg_len, sig, sig_len);
}
int OQS_SIGN_verify(const OQS_SIGN *scheme, OQS_RAND *rand, const uint8_t *pub, const uint8_t *msg, const size_t msg_len, const uint8_t *sig, const size_t sig_len) {
	return scheme->verify(rand, pub, msg, msg_len, sig, sig_len);
}

int main() {
	return 0;
}
~~~
