+        states[i].stackoffset = (unsigned int)bytes_to_ull(sk, 4);
+        sk += 4;
+
+        states[i].stacklevels = sk;
+        sk += params->tree_height + 1;
+
+        states[i].auth = sk;
+        sk += params->tree_height * params->n;
+
+        states[i].keep = sk;
+        sk += (params->tree_height >> 1) * params->n;
